function onThemeChanged(){const n=document.body,t=document.getElementById("dark-mode-toggle");if(t){let s=n.classList.contains("colorscheme-dark")?"light":"dark";t.setAttribute("aria-pressed",s==="dark"),t.setAttribute("aria-label",s==="dark"?"Switch to Light Mode":"Switch to Dark Mode");const e=t.querySelector("i");s==="dark"?(e.classList.remove("fa-sun"),e.classList.add("fa-moon")):(e.classList.remove("fa-moon"),e.classList.add("fa-sun"))}}document.addEventListener("DOMContentLoaded",function(){try{Particles.init({selector:".background",color:["#333333","#666666","#999999"],opacity:.3,connectParticles:!0,responsive:[{breakpoint:768,options:{color:["#555555","#888888","#CCCCCC"],maxParticles:40,connectParticles:!0}}]})}catch(e){console.error("Particles.js initialization failed:",e)}}),document.addEventListener("themeChanged",onThemeChanged)